using ApiProxy.Dal;
using ApiProxy.Logic;
using ApiProxy.Logic.Boundaries;
using Microsoft.AspNetCore.Authentication.JwtBearer;
using Microsoft.IdentityModel.Tokens;
using Serilog;

namespace ApiProxy.UI;

public class Program
{
    public static void Main(string[] args)
    {
        DependencyResolution(ServiceLocator.Current);
        AspConfigurationAndRun(args);
    }

    private static void DependencyResolution(IServiceLocator serviceLocator)
    {
        serviceLocator.Add<IRepository>(_ => new DevelopmentRepository())
            .Add(_ => new HttpClient())
            .AddSingleton(sl => new Logic.Tools(sl))
            .AddSingleton(sl => new Accounting(sl))
            .AddSingleton(sl => new Api(sl, "https://api.staging.pay2play.cash", "https://MyDomain/Callback/1f8976d4-149e-4aa0-89aa-e766d89cfc7d/", "eyJhbGciOiJIUzUxMiIsInR5cCI6IkpXVCJ9..K0OxFBt4SgrAGCNlGrAQ2krbBtfr1eM45Ph_MsMcuOEzRu1fZHCCL9O59EpdMzHkU72pj3E8G9tWiTPblZFsEw"));
    }
    private static void AspConfigurationAndRun(string[] args)
    {
        Log.Logger = new LoggerConfiguration()
            .WriteTo.File("logs/ApiProxy.txt", rollingInterval: RollingInterval.Day, retainedFileCountLimit: 3)
            .CreateLogger();

        var builder = WebApplication.CreateBuilder(args);
        builder.Services.AddControllersWithViews();
        builder.Services.AddAuthorization();
        builder.Services.AddAuthentication(JwtBearerDefaults.AuthenticationScheme).AddJwtBearer(options =>
        {
            options.TokenValidationParameters = new TokenValidationParameters
            {
                ValidateIssuer = true,
                ValidIssuer = AuthOptions.ISSUER,
                ValidateAudience = true,
                ValidAudience = AuthOptions.AUDIENCE,
                ValidateLifetime = true,
                IssuerSigningKey = AuthOptions.KEY,
                ValidateIssuerSigningKey = true,
                ClockSkew = TimeSpan.FromSeconds(1),
                NameClaimType = "MerchantGuid",
                RoleClaimType = "IdentityRole"
            };
        });

        var app = builder.Build();

        app.UseHttpsRedirection();
        app.UseStaticFiles();

        app.UseRouting();

        app.UseAuthentication();
        app.UseAuthorization();

        app.MapControllers();

        app.Run();
    }
}